!Fortran 2003 bindings for xraylib


#define _NIST_LIST_STRING_LENGTH 100
#define _RADIO_NUCLIDE_STRING_LENGTH 10
#define _CRYSTAL_STRING_LENGTH 100

MODULE xraylib

USE, INTRINSIC :: ISO_C_BINDING
IMPLICIT NONE

PUBLIC
PRIVATE :: stringF2C, process_error, convert_to_crystal_c

TYPE :: compoundData
        INTEGER (C_INT) :: nElements
        REAL (C_DOUBLE) :: nAtomsAll
        INTEGER (C_INT),DIMENSION(:),ALLOCATABLE:: Elements
        REAL (C_DOUBLE),DIMENSION(:),ALLOCATABLE:: massFractions
        REAL (C_DOUBLE),DIMENSION(:),ALLOCATABLE:: nAtoms
        REAL (C_DOUBLE) :: molarMass
ENDTYPE

TYPE, BIND(C) :: xrlComplex_C
        REAL (C_DOUBLE) :: re
        REAL (C_DOUBLE) :: im
ENDTYPE

TYPE, BIND(C) :: Crystal_Atom
        INTEGER (C_INT) :: Zatom
        REAL (C_DOUBLE) :: fraction
        REAL (C_DOUBLE) :: x
        REAL (C_DOUBLE) :: y
        REAL (C_DOUBLE) :: z
ENDTYPE

TYPE, BIND(C) :: Crystal_Struct_C
        TYPE (C_PTR) :: name
        REAL (C_DOUBLE) :: a
        REAL (C_DOUBLE) :: b
        REAL (C_DOUBLE) :: c
        REAL (C_DOUBLE) :: alpha
        REAL (C_DOUBLE) :: beta
        REAL (C_DOUBLE) :: gamma
        REAL (C_DOUBLE) :: volume
        INTEGER (C_INT) :: n_atom
        TYPE (C_PTR) :: atom
ENDTYPE

TYPE :: Crystal_Struct
        CHARACTER (KIND=C_CHAR,LEN=40) :: name
        REAL (C_DOUBLE) :: a
        REAL (C_DOUBLE) :: b
        REAL (C_DOUBLE) :: c
        REAL (C_DOUBLE) :: alpha
        REAL (C_DOUBLE) :: beta
        REAL (C_DOUBLE) :: gamma
        REAL (C_DOUBLE) :: volume
        INTEGER (C_INT) :: n_atom
        TYPE (Crystal_Atom), DIMENSION(:), ALLOCATABLE :: atom
ENDTYPE

TYPE :: compoundDataNIST
        CHARACTER(LEN=_NIST_LIST_STRING_LENGTH, KIND=C_CHAR):: name
        INTEGER (C_INT) :: nElements
        INTEGER (C_INT), ALLOCATABLE, DIMENSION(:) :: Elements
        REAL (C_DOUBLE), ALLOCATABLE, DIMENSION(:)  :: massFractions
        REAL (C_DOUBLE) :: density
ENDTYPE

TYPE, BIND(C) :: Crystal_Array
        INTEGER (C_INT) :: n_crystal
        INTEGER (C_INT) :: n_alloc
        TYPE (C_PTR) :: crystal
ENDTYPE

TYPE :: radioNuclideData
        CHARACTER(LEN=_RADIO_NUCLIDE_STRING_LENGTH, KIND=C_CHAR):: name
        INTEGER (C_INT) :: Z
        INTEGER (C_INT) :: A
        INTEGER (C_INT) :: N
        INTEGER (C_INT) :: Z_xray
        INTEGER (C_INT) :: nXrays
        INTEGER (C_INT), ALLOCATABLE, DIMENSION(:) :: XrayLines
        REAL (C_DOUBLE), ALLOCATABLE, DIMENSION(:) :: XrayIntensities
        INTEGER (C_INT) :: nGammas
        REAL (C_DOUBLE), ALLOCATABLE, DIMENSION(:) :: GammaEnergies
        REAL (C_DOUBLE), ALLOCATABLE, DIMENSION(:) :: GammaIntensities
ENDTYPE

ENUM, BIND(C)
        ENUMERATOR :: XRL_ERROR_MEMORY ! set in case of a memory allocation problem
        ENUMERATOR :: XRL_ERROR_INVALID_ARGUMENT ! set in case an invalid argument gets passed to a routine
        ENUMERATOR :: XRL_ERROR_IO ! set in case an error involving input/output occurred
        ENUMERATOR :: XRL_ERROR_TYPE ! set in case an error involving type conversion occurred
        ENUMERATOR :: XRL_ERROR_UNSUPPORTED ! set in case an unsupported feature has been requested
        ENUMERATOR :: XRL_ERROR_RUNTIME ! set in case an unexpected runtime error occurred
ENDENUM

TYPE :: xrl_error
        INTEGER (KIND(xrl_error_memory)) :: code
        CHARACTER(LEN=512, KIND=C_CHAR) :: message 
ENDTYPE

