query_schema:
query getDiseaseDbXRefsByName($name: String!) {
  search(queryString: $name, entityNames: ["disease"], page: { index: 0, size: 1 }) {
    hits {
      id
      name
      object {
        ... on Disease {
          id
          name
          dbXRefs
        }
      }
    }
  }
}

query getDiseaseDbXRefs($efoId: String!) {
  disease(efoId: $efoId) {
    id
    name
    dbXRefs
  }
}

query mapAnyDiseaseIdToDbXRefs($inputId: String!) {
  mapIds(queryTerms: [$inputId], entityNames: ["disease"]) {
    mappings {
      term
      hits {
        id
        name
        object {
          ... on Disease {
            id
            name
            dbXRefs
          }
        }
      }
    }
  }
}
